// Function to get a random word of the day
export const getWordOfTheDay = (): Word => {
  const today = new Date();
  const dayOfYear = Math.floor((today.getTime() - new Date(today.getFullYear(), 0, 0).getTime()) / 86400000);
  const index = dayOfYear % vocabulary.length;
  return vocabulary[index];
};

// Function to get words by category
export const getWordsByCategory = (categoryId: string): Word[] => {
  return vocabulary.filter(word => word.category === categoryId);
};

// Function to get words by level
export const getWordsByLevel = (level: string): Word[] => {
  return vocabulary.filter(word => word.level === level);
};

// Function to search words
export const searchWords = (query: string): Word[] => {
  const lowercaseQuery = query.toLowerCase();
  return vocabulary.filter(word => 
    word.german.toLowerCase().includes(lowercaseQuery) ||
    word.arabic.includes(query) ||
    word.pronunciation.toLowerCase().includes(lowercaseQuery)
  );
};